INSERT INTO products (active, brand_id, category_id, created_date, id, updated_date, thumbnail, description, name) VALUES ('true', 4, 6, '2024-11-18 22:35:11', 15, '2024-12-03 11:39:29', 'http://res.cloudinary.com/dvyx6ldqz/image/upload/v1731944113/qmqezdmqoiaubanfiyos.jpg', 'Chuốt mi 3CE Slim Fix Waterproof Mascara đến từ thương hiệu nổi tiếng 3CE có thiết kế đầu chải cong hình hạt đậu làm cho việc tạo góc cong và xử lý các khu vực khóe mi và hàng mi dưới trở nên dễ dàng hơn bao giờ hết. Kết cấu mascara chống lem trôi giúp duy trì độ cong và tạo độ dày, dài cho hàng mi một cách hiệu quả.', 'Chuốt Mi 3CE Slim Fix Waterproof Mascara');
