INSERT INTO products (active, brand_id, category_id, created_date, id, updated_date, thumbnail, description, name) VALUES ('true', 352, 25, '2024-11-19 00:13:14', 37, '2024-11-19 00:13:28', 'http://res.cloudinary.com/dvyx6ldqz/image/upload/v1731949996/nhfeiroyihhqod0pyqoj.jpg', 'Che Khuyết Điểm Lemonade Matte Addict Concealer 3g đến từ thương hiệu mỹ phẩm Việt Lemonade. Sản phẩm nổi bật với kết cấu dạng kem mỏng nhẹ, dễ tán đều nhưng vẫn có độ che phủ cao, làm mờ khuyết điểm nhanh chóng, đem đến lớp nền mịn lì không tì vết. Thêm vào đó, che khuyết điểm còn có độ bền màu cao, duy trì màu lên đến 10 tiếng, cho bạn lớp trang điểm ổn định suốt cả ngày dài.', 'Che Khuyết Điểm Lemonade');
INSERT INTO products (active, brand_id, category_id, created_date, id, updated_date, thumbnail, description, name) VALUES ('true', 364, 25, '2024-11-19 00:15:40', 38, '2024-11-19 00:15:48', 'http://res.cloudinary.com/dvyx6ldqz/image/upload/v1731950142/u9yejcevsfwi9rutdh3p.jpg', 'Che Khuyết Điểm Luk Fixx Dành Cho Tóc Bạc Hói đến từ thương hiệu mỹ phẩm Hàn Quốc Luk. Sản phẩm nổi bật với công nghệ Lookfix có khả năng che phủ tóc hói, khu vực tóc không đều màu vô cùng tự nhiên, chỉ với một lần chạm.', 'Che Khuyết Điểm Luk Fixx Dành Cho Tóc Bạc Hói - Đen');
INSERT INTO products (active, brand_id, category_id, created_date, id, updated_date, thumbnail, description, name) VALUES ('true', 234, 25, '2024-11-19 00:20:46', 39, '2024-11-19 00:20:52', 'http://res.cloudinary.com/dvyx6ldqz/image/upload/v1731950446/ewwffvyxrhiwb9wbgo2i.jpg', 'Kem che khuyết điểm The Saem Cover Perfection Tip Concealer SPF28/PA++ là một trong số các sản phẩm nổi tiếng nhất của thương hiệu The Saem bởi khả năng che phủ khuyết điểm, thâm, sẹo, mụn… đạt đến độ hoàn hảo. Cùng với đó là mức giá phải chăng mà ai cũng có thể sở hữu được.', 'Che Khuyết Điểm Thesame');
