INSERT INTO products (active, brand_id, category_id, created_date, id, updated_date, thumbnail, description, name) VALUES ('true', 422, 34, '2024-11-19 00:42:25', 44, '2024-11-19 00:42:34', 'http://res.cloudinary.com/dvyx6ldqz/image/upload/v1731951747/dswfgodm2mt9gbjtgxwx.jpg', 'Nước Tẩy Trang Simple Water Boost Cấp Ẩm 400ml là sự lựa chọn tuyệt vời cho làn da khô và nhạy cảm. Với công thức Micellar Water chứa các hạt micelles siêu nhỏ, sản phẩm không chỉ làm sạch sâu lớp trang điểm và bụi bẩn mà còn làm dịu và cung cấp độ ẩm tức thì cho da, giúp da luôn tươi mát, mềm mại mà không gây kích ứng.', 'Nước Tẩy Trang Simple');
INSERT INTO products (active, brand_id, category_id, created_date, id, updated_date, thumbnail, description, name) VALUES ('true', 296, 34, '2024-11-19 00:44:27', 45, '2024-11-19 00:44:37', 'http://res.cloudinary.com/dvyx6ldqz/image/upload/v1731951869/gaupoflx6jhehyz0t51u.webp', 'Nước tẩy trang Nuxe Very Rose 3 trong 1 kết hợp nước hoa hồng để loại bỏ hiệu quả lớp trang điểm, các tạp chất trên da, làm sạch da mặt và mắt. Được bổ sung phức hợp Skin-Respect Complex 100% nguồn gốc thực vật, nó làm dịu làn da từ khô đến rất khô, mang lại cảm giác thoải mái và giữ cân bằng cho da. ', 'Nước Tẩy Trang Nuxe Very Rose');
INSERT INTO products (active, brand_id, category_id, created_date, id, updated_date, thumbnail, description, name) VALUES ('true', 145, 34, '2024-11-19 00:46:46', 46, '2024-11-19 00:46:54', 'http://res.cloudinary.com/dvyx6ldqz/image/upload/v1731952006/n2iuuqyyfir6hy6oprex.webp', 'Nước Tẩy Trang Eucerin Sensitive Skin đến từ thương hiệu mỹ phẩm nổi tiếng của Pháp Eucerin nổi bật với 3 công dung dưỡng da trong 1 sản phẩm, bao gồm: tẩy trang, rửa mặt và cân bằng da.', 'Nước Tẩy Trang Eucerin');
