INSERT INTO products (active, brand_id, category_id, created_date, id, updated_date, thumbnail, description, name) VALUES ('true', 341, 34, '2024-11-19 00:51:52', 48, '2024-11-19 00:52:03', 'http://res.cloudinary.com/dvyx6ldqz/image/upload/v1731952313/ncaqu6znyij7wkmiayjt.jpg', 'Làm sạch là một bước rất quan trọng trong quá trình chăm sóc da. Một ngày da mặt phải tiếp xúc với bụi bẩn, ô nhiễm từ môi trường bên ngoài và mồ hôi, bã nhờn, tế bào da chết,... từ chính da mặt tiết ra, lâu dần, sẽ gây ra tình trạng mụn, viêm da,... Do đó, cần thiết phải có một sản phẩm giúp loại bỏ hết những tác nhân gây hại đó ra khỏi làn da của chúng ta. Nếu bạn đang tìm một sản phẩm làm sạch sâu, an toàn lại dịu nhẹ thì Nước Tẩy Trang L''Oreal Micellar Water 400ml chính là sự lựa chọn hoàn hảo.
INSERT INTO products (active, brand_id, category_id, created_date, id, updated_date, thumbnail, description, name) VALUES ('true', 362, 58, '2024-11-19 00:54:04', 49, '2024-11-19 00:54:11', 'http://res.cloudinary.com/dvyx6ldqz/image/upload/v1731952446/tz3qoja1tj958spffsax.webp', 'Sữa Tắm Lux Botanicals Hoa Hồng Pháp Nồng Nàn 570g và Hương Phong Lan Quyến Rũ là bộ đôi sản phẩm đến từ nhà Lux. Sản phẩm có hương nước hoa cao cấp, khó cưỡng cùng nhiều dưỡng chất giúp da bạn luôn mềm mịn và lưu hương lâu.', 'Sữa Tắm Lux Botanicals Hương Hoa Thiên Điểu');
INSERT INTO products (active, brand_id, category_id, created_date, id, updated_date, thumbnail, description, name) VALUES ('true', 332, 58, '2024-11-19 00:56:07', 50, '2024-11-19 00:56:13', 'http://res.cloudinary.com/dvyx6ldqz/image/upload/v1731952567/z8tjxf44bsibgup3zipd.webp', 'Sữa tắm là một trong những sản phẩm chăm sóc da body được nhiều người yêu thích. Sản phẩm có khả năng làm sạch, bổ sung các dưỡng chất nuôi dưỡng làn da mềm mại, khoẻ đẹp cũng như tạo hương thơm cho cơ thể.', 'Sữa Tắm Olay Hương Dâu & Bạc Hà');
INSERT INTO products (active, brand_id, category_id, created_date, id, updated_date, thumbnail, description, name) VALUES ('true', 193, 58, '2024-11-19 00:58:10', 51, '2024-11-19 00:58:16', 'http://res.cloudinary.com/dvyx6ldqz/image/upload/v1731952690/s7p3bxyee1509yc5apab.webp', 'Sữa Tắm Cow Bouncia Body Soap 500ml là dòng sản phẩm chăm sóc cơ thể đến từ thương hiệu Bouncia của Nhật Bản. Các sản phẩm của hãng đều được đánh giá là có chất lượng tốt, phân khúc bình dân nên nhanh chóng trở thành sự lựa chọn hàng đầu của người Nhật và khắp châu Á. 
INSERT INTO products (active, brand_id, category_id, created_date, id, updated_date, thumbnail, description, name) VALUES ('true', 54, 83, '2024-11-26 23:25:46', 52, '2024-11-26 23:25:52', 'http://res.cloudinary.com/dvyx6ldqz/image/upload/v1732638348/xnhtgbwxu22fwfrjtatp.webp', 'Kem nền Bobbi Brown kiềm dầu lâu trôi 30ml là sản phẩm trang điểm cao cấp đến từ thương hiệu danh tiếng Bobbi Brown. Sản phẩm đem đến cho bạn một trải nghiệm trang điểm tối ưu với một lớp nền tự nhiên và tươi tắn.Với khả năng duy trì trên da lên đến 16 giờ, bền màu chống chuyển hóa và không gây mụn. Độ che phủ linh hoạt, từ trung bình đến full coverage, giúp che đi mọi khuyết điểm. Các hạt màu độc đáo giúp che đi những vấn đề như nếp nhăn, thâm mụn cho đến lỗ chân lông to, vùng da không đều màu và vết mẩn đỏ. Mang lại một lớp nền đa chiều khi phản chiếu ánh sáng.', 'Kem Nền Bobbi Brown');
INSERT INTO products (active, brand_id, category_id, created_date, id, updated_date, thumbnail, description, name) VALUES ('true', 259, 83, '2024-11-26 23:29:10', 53, '2024-11-26 23:29:19', 'http://res.cloudinary.com/dvyx6ldqz/image/upload/v1732638552/mzxl4ht5mx4phasqsx2v.webp', 'Sản phẩm được đặc chế dành riêng cho làn da nhạy cảm của trẻ em, mang đến khả năng bảo vệ da trước tác hại của tia UV, đồng thời cung cấp độ ẩm cần thiết để làn da bé luôn mềm mịn, khỏe mạnh.
INSERT INTO products (active, brand_id, category_id, created_date, id, updated_date, thumbnail, description, name) VALUES ('true', 369, 83, '2024-11-26 23:31:09', 54, '2024-11-26 23:31:16', 'http://res.cloudinary.com/dvyx6ldqz/image/upload/v1732638671/ltjrfgoy1up362j7rhcd.webp', 'Xịt Khoáng Khóa Lớp Trang Điểm Có Nhũ MAC Prep + Prime Face Fix + (Shimmer) 100ml là sản phẩm của thương hiệu MAC của Mỹ được ưa chuộng cực kì bởi khả năng cấp ẩm và giữ lớp trang điểm bền màu, mướt mát. Nay fan hâm mộ yêu thích của xịt khoáng Fix + giờ đây đã có thêm một sự lựa chọn mới với Fix + (Shimmer) có kết cấu thêm ánh nhũ ngọc trai cho làn da tỏa sáng. Công thức này vẫn có tất cả những công dụng của phiên bản gốc như dưỡng ẩm, làm dịu da và định hình lớp trang điểm, nhưng với phiên bản này, Xịt khoáng khoá lớp trang điểm MAC Prep + Prime Face Fix + (Shimmer) 100ml có thêm tính năng cải thiện thời gian trang điểm lâu trôi và bền màu đến 12h đồng thời bổ sung thêm tinh chất ngọc trai cho hiệu ứng làn da trông sáng khỏe, căng bóng và rạng rỡ hơn.', 'Xịt Khóa Lớp Trang Điểm MAC Có Nhũ');
INSERT INTO products (active, brand_id, category_id, created_date, id, updated_date, thumbnail, description, name) VALUES ('true', 54, 83, '2024-11-26 23:34:46', 55, '2024-11-26 23:34:50', 'http://res.cloudinary.com/dvyx6ldqz/image/upload/v1732638888/hkapwnevzrcqhoyc41rf.jpg', 'Kem Lót Dưỡng Ẩm Bobbi Brown Vitamin Enriched Face Base 50ml là sự kết hợp đặc biệt giữa kem lót và kem dưỡng da. Không chỉ tạo lớp nền hoàn hảo cho lớp trang điểm, sản phẩm còn cung cấp các dưỡng chất cần thiết để nuôi dưỡng da, giúp da luôn mềm mịn và tươi trẻ. 
INSERT INTO products (active, brand_id, category_id, created_date, id, updated_date, thumbnail, description, name) VALUES ('true', 54, 83, '2024-11-26 23:36:43', 56, '2024-11-26 23:36:49', 'http://res.cloudinary.com/dvyx6ldqz/image/upload/v1732639004/jydwh3qqkmezzutp8u8q.webp', 'Phấn Phủ Bobbi Brown Sheer Finish Pressed Powder 11g đến từ thương hiệu mỹ phẩm đình đám của Mỹ Bobbi Brown. Sản phẩm nổi bật với kết cấu dạng phấn nén với chất phấn mỏng nhẹ, thoáng khí. Khi apply lên da, phấn nhanh chóng tiệp nhanh vào da một cách tự nhiên, đem đến lớp nền trang điểm mịn màng, tinh tế.
INSERT INTO products (active, brand_id, category_id, created_date, id, updated_date, thumbnail, description, name) VALUES ('true', 22, 80, '2024-11-26 23:40:27', 57, '2024-11-26 23:40:30', 'http://res.cloudinary.com/dvyx6ldqz/image/upload/v1732639228/nsfj1muhjutlde5r9oke.webp', 'Armaf Club De Nuit EDT Intense Man là một trong những mùi hương nổi tiếng nhất của thương hiệu nước hoa Armaf. Được giới thiệu lần đầu vào năm 2015, đây là một mùi hương tinh tế và quyến rũ dành cho nam giới.
INSERT INTO products (active, brand_id, category_id, created_date, id, updated_date, thumbnail, description, name) VALUES ('true', 78, 80, '2024-11-26 23:42:50', 58, '2024-11-26 23:42:54', 'http://res.cloudinary.com/dvyx6ldqz/image/upload/v1732639372/lvqpq7dx1xs5kvxaem5k.webp', 'Nước Hoa Versace Eros EDT được ra mắt vào năm 2012 bởi nhà sáng chế nước hoa nổi tiếng Aurelien Guichard. Sản phẩm được lấy cảm hứng từ vị thần tình yêu Eros trong thần thoại Hy Lạp. Khi cho ra đời dòng nước hoa này, giám đốc điều hành của Versace, Donatella Versace chia sẻ: “Tôi đã tưởng tượng ra một người đàn ông anh hùng, đầy nhiệt huyết, gần như là một vị thần Hy Lạp. Hương thơm được tạo thành từ những nốt hương thể hiện sự gợi cảm và quyền lực, một sức mạnh tột độ của phái mạnh”.
